Description # Key Takeaways # Combining DS skill to a real project. Experience on merging model develpment with backend(Django) and frontend(React). Agile Management. Here I also exposed to the full agile software development cycle of a product Method to improve the models Where to improve # Use full training set to increase accuracy and F1 perfromance, reducee False positive and Falues Negative Different method to preprocess the data Function Code # # For Data manipulation import pandas as pd import numpy as np # For Data Visulization import matplotlib.pyplot as plt import seaborn as sns #For Modelling and evaluation from sklearn.model_selection import train_test_split,StratifiedKFold, cross_val_score from xgboost import XGBClassifier import lightgbm as lgb from sklearn.ensemble import RandomForestClassifier,AdaBoostClassifier,BaggingClassifier,StackingClassifier from sklearn import metrics from sklearn.preprocessing import StandardScaler from sklearn.model_selection import GridSearchCV, RandomizedSearchCV from sklearn.neighbors import KNeighborsClassifier as KNN #For text preprocessing from sklearn.feature_extraction.text import TfidfVectorizer #For model parameter saving and loading import pickle # Import dataset df = pd.read_csv(&#39;data.csv&#39;,on_bad_lines=&#39;skip&#39;) #ignore badline from dataset df.isna().sum() df.duplicated().sum() #only 1 missing value, and no duplicate found,will drop the bad record directly df.dropna(inplace = True) #Import text data for all the weak passwords from rock you leak df2 = pd.read_csv(&#39;rockyou.txt&#39;,delimiter=&#39;	&#39;,header = None, names = [&#39;password&#39;],encoding=&#39;ISO-8859-1&#39;) df2.dropna(inplace = True) df2.drop_duplicates(inplace = True) df2[&#39;strength&#39;] = 0 df_full = pd.concat([df,df2],ignore_index=True) # Compute the value counts of the Gender column value_counts = df_full[&#39;strength&#39;].value_counts() # Set the number of samples to be drawn from each group n_samples = value_counts.min() # Group the dataframe by Gender and sample n_samples from each group sampled_df = df.groupby(&#39;strength&#39;).apply(lambda x: x.sample(n=n_samples)).reset_index(drop=True) # Print the sampled dataframe print(sampled_df) X = sampled_df[&#39;password&#39;] y = sampled_df[&#39;strength&#39;] #tokenize password vectorizer = TfidfVectorizer(analyzer = &#39;char&#39;) X = vectorizer.fit_transform(X) #Save the vectorizer for backend use with open(&#34;vectorizer2.pkl&#34;, &#34;wb&#34;) as f: pickle.dump(vectorizer, f) #Data preprocessing def train_val_test_split(X,y,ratio): X_train,X_,y_train,y_ = train_test_split(X,y,test_size=ratio,stratify=y,random_state=1) X_val,X_test,y_val,y_test = train_test_split(X_,y_,test_size=.5,stratify=y_,random_state=1) return X_train,X_val,X_test,y_train,y_val,y_test X_train,X_val,X_test,y_train,y_val,y_test = train_val_test_split(X,y,ratio=.25) #Model Selection with CV models = [] # Empty list to store all the models # Appending models into the list models.append((&#34;Random forest&#34;, RandomForestClassifier(random_state=1))) models.append((&#34;Bagging&#34;, BaggingClassifier(random_state=1))) models.append((&#34;Xgboost&#34;, XGBClassifier(random_state=1, eval_metric=&#34;logloss&#34;))) models.append((&#34;lgbm&#34;, lgb.LGBMClassifier(random_state=1))) models.append((&#39;KNN&#39;,KNN())) results = [] # Empty list to store all model&#39;s CV scores names = [] # Empty list to store name of the models score = [] # loop through all models to get the mean cross validated score print(&#34;
&#34; &#34;Cross-Validation Performance:&#34; &#34;
&#34;) # Use F1 since for password evaluation I want to ensure to minimal both FP and FN rate for name, model in models: kfold = StratifiedKFold( n_splits=5, shuffle=True, random_state=1 ) # Setting number of splits equal to 5 cv_result = cross_val_score( estimator=model, X=X_train, y=y_train, scoring=&#39;f1_macro&#39;, cv=kfold ) results.append(cv_result) names.append(name) print(&#34;{}: {}&#34;.format(name, cv_result.mean())) #Form CV result, XGBoost generate around 98.4% marco f1 score #Using Xgboost provided the best f1_marco result, thus we can fine tune it # defining model - XGBoost Hyperparameter Tuning model = XGBClassifier(random_state=1, eval_metric=&#34;logloss&#34;) # Parameter grid to pass in RandomizedSearchCV param_grid = { &#34;n_estimators&#34;: np.arange(150, 300, 50), &#34;learning_rate&#34;: [0.0001,0.001,0.01,0.0015], &#34;gamma&#34;: [0, 3, 5,7], &#34;subsample&#34;: [0.5, 0.9,0.2,0.35], &#39;reg_alpha&#39;:[0,1], &#39;reg_lambda&#39;:[0,1] } # Calling RandomizedSearchCV randomized_cv = RandomizedSearchCV( estimator=model, param_distributions=param_grid, n_iter=20, # sample 20 setting scoring=&#39;f1_macro&#39;, cv=3, random_state=1, n_jobs=-1, ) # Fitting parameters in RandomizedSearchCV randomized_cv.fit(X_train, y_train) print( &#34;Best parameters are {} with CV score={}:&#34;.format( randomized_cv.best_params_, randomized_cv.best_score_ ) ) best_model = XGBClassifier( **randomized_cv.best_params_,eval_metric = &#39;logloss&#39;,random_state = 1 )# building model with best parameters print(&#34;
